package com.example.android56_homework_day6_recyclerview;

import android.content.Intent;
import android.os.Bundle;
import android.util.Log;
import android.widget.Toast;

import androidx.activity.EdgeToEdge;
import androidx.appcompat.app.AppCompatActivity;
import androidx.core.graphics.Insets;
import androidx.core.view.ViewCompat;
import androidx.core.view.WindowInsetsCompat;
import androidx.recyclerview.widget.GridLayoutManager;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.example.android56_homework_day6_recyclerview.adapters.ProductAdapter;
import com.example.android56_homework_day6_recyclerview.interfaces.ProductClickListener;
import com.example.android56_homework_day6_recyclerview.models.Product;

import java.util.ArrayList;

public class MainActivity extends AppCompatActivity {
    private static final String TAG = "MainActivity";
    private ArrayList<Product> mListProduct;
    private ProductAdapter mProductAdapter;
    private RecyclerView rvProduct;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        EdgeToEdge.enable(this);
        setContentView(R.layout.activity_main);

        initData();
        initView();
    }

    private void initView() {
        rvProduct = findViewById(R.id.rvProduct);

        mProductAdapter = new ProductAdapter(mListProduct);
        LinearLayoutManager linearLayoutManager = new LinearLayoutManager(this, LinearLayoutManager.VERTICAL, false);
        rvProduct.setLayoutManager(linearLayoutManager);
        rvProduct.setAdapter(mProductAdapter);

        mProductAdapter.setmProductClickListener(new ProductClickListener() {
            @Override
            public void onItemClickListener(int position, Product product) {
                Intent intent = new Intent(MainActivity.this, ProductDetailActivity.class);
                intent.putExtra("product_detail", product);
                startActivity(intent);
            }


        });
    }

    private void initData() {
        mListProduct = new ArrayList<>();
        for (int i = 0; i < 20; i++) {
            Product product = new Product();
            product.setTitle("Product " + i);
            product.setDescription("Description of Product " + i);
            product.setPrice(100 + i * 10);
            product.setRating(4.5f);
            product.setReviews(100 + i);
            product.setImageResId("data:image/jpeg;base64,"); // Replace with actual image resource
            mListProduct.add(product);
        }
    }
}